import { useState } from 'react';
import './App.css';
const HeaderTest = () => {
  const [degisken,stateDegisken] = useState("Ben baska bir sınıftan geldim");
  const [degisim,stateDegisim] = useState(true);
  const [count,stateCount] = useState(0);
  function ButtonClick () {
   stateDegisim(!degisim)
    if(degisim===true)
    {
      stateDegisken("Erdem Bir Sus Ya!!!");
    }else{
      stateDegisken("Yunus Bir Sus Ya!!!");
    }
  }
  function PlusButton () {
    stateCount++;
   }
   function MinusButton () {
    stateCount--;
   }
  const textStyle = {
    backgroundColor: degisim ? "green" : "red",
    color: "white",
    padding: "10px",
    borderRadius: "5px",
    cursor: "pointer"
  };



  return (
      <div>
            <p style={textStyle}>{degisken}</p><br/>
            <input placeholder="Buraya Yaz"></input> <br/>
            <button onClick={ButtonClick} className='ButtonStyle'>Buraya Tıkla</button>
            <p style={textStyle}>{count}</p><br/>
            <div>
              <button onClick={PlusButton} className='ButtonStyle'>+</button>
              <button onClick={MinusButton} className='ButtonStyle'>-</button>
            </div>
      </div>

  )
}

export default HeaderTest
